INSERT [dbo].[Document] ([Id], [Type], [Content], [Version]) VALUES (307, N'OrchardCore.ContentManagement.ContentItem, OrchardCore.ContentManagement.Abstractions', N'{"ContentItemId":"4fwz29qk80pmp5y4ezyspqa845","ContentItemVersionId":"4gn3ccz0xbbz2zp2wyw64grhnb","ContentType":"News","DisplayText":"بازدید ویژه وزیر نیرو از شرکت دانش بنیان پارس سویچ","Latest":false,"Published":false,"ModifiedUtc":"2022-07-26T11:08:39.5428333Z","PublishedUtc":"2022-07-26T11:08:39.5574725Z","CreatedUtc":"2022-07-26T11:08:39.5428333Z","Owner":"4r4a0rm6448stxperjmgs32n56","Author":"admin","News":{"NewsBody":{"Html":"<p>\n  وزیر نیرو ، جناب آقای مهندس محرابیان در بازدید ویژه از شرکت دانش بنیان پارس سویچ، از توانمندی‌های این شرکت بزرگ و موثر در زمینه تولید و تامین تجهیزات صنعت برق دیدن و بر اهمیت این شرکت در تامین تجهیزات ایمن شبکه برق ایران تاکید نمودند.\n\nاین بازدید که به دعوت مدیر عامل شرکت پارس سویچ آقای مهندس نظری و خارج از برنامه بازدید وزیر محترم نیرو از صنعت آب و برق استان زنجان صورت گرفت ، آقای دکتر افشارچی استاندار زنجان و نمایندگان محترم استان زنجان در مجلس شورای اسلامی نیز ایشان را همراهی نمودند.\n</p>"},"Id":{"Value":3.0},"NewsDate":{"Value":"2022-07-26T00:00:00Z"},"NewsOverview":{"Html":"<p>\n  وزیر نیرو ، جناب آقای مهندس محرابیان در بازدید ویژه از شرکت دانش بنیان پارس سویچ، از توانمندی‌های این شرکت بزرگ و موثر در زمینه تولید و تامین تجهیزات صنعت برق دیدن و بر اهمیت این شرکت در تامین تجهیزات ایمن شبکه برق ایران تاکید نمودند.\n\nاین بازدید که به دعوت مدیر عامل شرکت پارس سویچ آقای مهندس نظری و خارج از برنامه بازدید وزیر محترم نیرو از صنعت آب و برق استان زنجان صورت گرفت ، آقای دکتر افشارچی استاندار زنجان و نمایندگان محترم استان زنجان در مجلس شورای اسلامی نیز ایشان را همراهی نمودند.\n</p>"},"NewsGroup":{"ContentItemIds":["4wc5g9926wnjwzf6mcnveghsc3"]}},"AliasPart":{"Alias":"بازدید-ویژه-وزیر-نیرو-از-شرکت-دانش-بنیان-پارس-سویچ"},"TitlePart":{"Title":"بازدید ویژه وزیر نیرو از شرکت دانش بنیان پارس سویچ"},"BagPart":{"ContentItems":[{"ContentItemId":"4z401hdb6bt8t71a2trzy36sxh","Conten