INSERT [dbo].[Document] ([Id], [Type], [Content], [Version]) VALUES (401, N'OrchardCore.ContentManagement.ContentItem, OrchardCore.ContentManagement.Abstractions', N'{"ContentItemId":"4rvcr64gfgr8arq5b0a4yaw3xb","ContentItemVersionId":"4btw3qqw9weg6x2944yggvasmt","ContentType":"HomeAboutUs","DisplayText":"تولید کننده حساس ترین تجهیزات صنعت برق با بالاترین کیفیت","Latest":false,"Published":false,"ModifiedUtc":"2022-08-01T03:45:16.6095272Z","PublishedUtc":"2022-08-01T03:45:16.6834118Z","CreatedUtc":"2022-08-01T03:45:16.6095272Z","Owner":"4r4a0rm6448stxperjmgs32n56","Author":"admin","HomeAboutUs":{"AboutUsImage":{"Paths":["1_2.jpg"],"MediaTexts":[""]}},"HtmlBodyPart":{"Html":"<p>\n  شرکت پارس سویچ با بهره گیری از فناوری شرکت های طراز اول جهان در صنعت برق همچون آلستوم فرانسه، آ.ب.ب سوئد و کالور -ایماگ آلمان، تولید سویچ های فشار قوی و متوسط را به عنوان يكي از حساس ترین تجهيزات صنعت برق، در کشور آغاز و پس از چند دهه تلاش مستمر با ارتقا توانمندي ها، به شرکتی پیشتاز در این حوزه تبديل شده است.\n</p>"},"TitlePart":{"Title":"تولید کننده حساس ترین تجهیزات صنعت برق با بالاترین کیفیت"}}', 4)
