INSERT [dbo].[Document] ([Id], [Type], [Content], [Version]) VALUES (705, N'OrchardCore.ContentManagement.ContentItem, OrchardCore.ContentManagement.Abstractions', N'{"ContentItemId":"412f5h38rdjs6r48dd3q7ceg42","ContentItemVersionId":"44p2fj2s6em6gz3tcxnx6y40wd","ContentType":"AboutUs","DisplayText":"منابع انسانی","Latest":false,"Published":false,"ModifiedUtc":"2022-08-20T04:36:42.8740604Z","PublishedUtc":"2022-08-20T04:36:42.893745Z","CreatedUtc":"2022-08-20T04:36:42.8740604Z","Owner":"4r4a0rm6448stxperjmgs32n56","Author":"admin","AboutUs":{},"AliasPart":{"Alias":"منابع-انسانی"},"AutoroutePart":{"Path":"منابع-انسانی-0","SetHomepage":false,"Disabled":false,"RouteContainedItems":false,"Absolute":false},"HtmlBodyPart":{"Html":"<p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><strong style=\"margin: 0; padding: 0\"> [image]AboutUs/1_1.jpg[/image]<br></strong></p><p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><strong style=\"margin: 0; padding: 0\">اهمیت منابع انسانی</strong></p><p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><span style=\"margin: 0; padding: 0\">سازمانهای موجود در هر جامعه با استفاده از منابع محدودی که در اختیار دارند، به گونه ای به رفع نیازهای فردی و گروهی افراد جامعه می پردازند و از آنجا که منابع تولید همواره با یک ویژگی یعنی کمیابی مواجه هستند، اغلب اصلی ترین هدف سازمانها استفاده بهینه از عوامل تولید و رسیدن به اثربخشی است. این عوامل شامل زمین، سرمایه، نیروی انسانی، فناوری و مدیریت است. در این بین نیروی انسانی، ارزشمندترین دارایی سازمانهای کنونی از جایگاه حساسی برخوردار است. عملکرد انسان در درون سازمان انعکاسی از دانش، مهارت و ارزشهای او است. امروزه موضوع نیروی انسانی بعنوان منابع انسانی مطرح است. زیرا هر فرد می تواند با ارزش آفرینی در سازمان، بعنوان منبعی الهام بخش برای ایجاد تحول و حرکت در سازمان باشد.</span></p><p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><strong style=\"margin: 0; padding: 0\">جدول آمار کارکنان از نظر سابقه کار در شرکت</strong></p><table style=\"text-align: right; margin: 0; padding: 0; direction: ltr; width: 1250px\"><tbody style=\"margin: 0; padding: 0\"><tr style=\"margin: 0; padding: 0\"><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">21 به بالا</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">16 تا 20</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">10 تا 15</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">5 تا 9</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">تا 4 سال</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">سابقه کار(سال)</td></tr><tr style=\"margin: 0; padding: 0\"><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">128</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">72</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">46</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">30</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">90</td><td style=\"margin: 0; padding: 0; border-width: 1px; border-color: rgba(221, 221, 221, 1)\">تعداد در سال 99</td></tr></tbody></table><p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><br style=\"margin: 0; padding: 0\"></p><p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><strong style=\"margin: 0; padding: 0\">جدول آمار کارکنان از نظر سن</strong></p><table style=\"text-align: right; margin: 0; padding: 0; direction: ltr; width: 1250px\"><tbody style=\"margin: 0; padding: 0\"><tr style=\"margin: 0; padding: 0\"><td style=\"margin: 0; padding: 0; border-width: 1px; border-colo