INSERT [dbo].[Document] ([Id], [Type], [Content], [Version]) VALUES (724, N'OrchardCore.ContentManagement.ContentItem, OrchardCore.ContentManagement.Abstractions', N'{"ContentItemId":"412f5h38rdjs6r48dd3q7ceg42","ContentItemVersionId":"4jxtbghbqr0jsw2mw6nc13ewnf","ContentType":"AboutUs","DisplayText":"منابع انسانی","Latest":false,"Published":false,"ModifiedUtc":"2022-08-20T05:51:01.1198251Z","PublishedUtc":"2022-08-20T05:51:01.1682872Z","CreatedUtc":"2022-08-20T04:36:42.8740604Z","Owner":"4r4a0rm6448stxperjmgs32n56","Author":"admin","AboutUs":{"AboutUsChart":{"ContentItemIds":["4pageba5qy7vezzk64q87s3n5q"]}},"AliasPart":{"Alias":"ABT-FA-3015"},"AutoroutePart":{"Path":"منابع-انسانی-0","SetHomepage":false,"Disabled":false,"RouteContainedItems":false,"Absolute":false},"HtmlBodyPart":{"Html":"<p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><strong style=\"margin: 0; padding: 0\"> [image]AboutUs/1_1.jpg[/image]<br></strong></p><p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><strong style=\"margin: 0; padding: 0\">اهمیت منابع انسانی</strong></p><p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><span style=\"margin: 0; padding: 0\">سازمانهای موجود در هر جامعه با استفاده از منابع محدودی که در اختیار دارند، به گونه ای به رفع نیازهای فردی و گروهی افراد جامعه می پردازند و از آنجا که منابع تولید همواره با یک ویژگی یعنی کمیابی مواجه هستند، اغلب اصلی ترین هدف سازمانها استفاده بهینه از عوامل تولید و رسیدن به اثربخشی است. این عوامل شامل زمین، سرمایه، نیروی انسانی، فناوری و مدیریت است. در این بین نیروی انسانی، ارزشمندترین دارایی سازمانهای کنونی از جایگاه حساسی برخوردار است. عملکرد انسان در درون سازمان انعکاسی از دانش، مهارت و ارزشهای او است. امروزه موضوع نیروی انسانی بعنوان منابع انسانی مطرح است. زیرا هر فرد می تواند با ارزش آفرینی در سازمان، بعنوان منبعی الهام بخش برای ایجاد تحول و حرکت در سازمان باشد.</span></p><p style=\"text-align: right; margin-right: 0; margin-left: 0; padding: 0\"><strong style=